import React, { useState, useEffect } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/swiper-bundle.min.css';
import 'swiper/components/effect-coverflow/effect-coverflow.min.css';
import 'swiper/components/pagination/pagination.min.css';
import 'swiper/components/navigation/navigation.min.css';
import SwiperCore, { EffectCoverflow, Pagination, Navigation } from 'swiper/core';
import axios from 'axios';

SwiperCore.use([EffectCoverflow, Pagination, Navigation]);

interface TourPackage {
  name: string;
  description: string;
  imageUrl: string;
}

const TourPackages: React.FC = () => {
  const [tourPackages, setTourPackages] = useState<TourPackage[]>([]);

  useEffect(() => {
    const fetchTourPackages = async () => {
      try {
        const response = await axios.get<TourPackage[]>('http://localhost:4561/api/tour-packages');
        setTourPackages(response.data);
      } catch (error) {
        console.error('Error fetching tour packages:', error);
      }
    };

    fetchTourPackages();
  }, []);

  useEffect(() => {
    const swiper = new Swiper('.swiper_container', {
      effect: 'coverflow',
      grabCursor: true,
      centeredSlides: true,
      loop: true,
      slidesPerView: 'auto',
      coverflowEffect: {
        rotate: 0,
        stretch: 0,
        depth: 100,
        modifier: 2.5,
      },
      pagination: { clickable: true },
      navigation: {
        nextEl: '.swiper-button-next',
        prevEl: '.swiper-button-prev',
      },
    });

    return () => {
      swiper.destroy(true, true);
    };
  }, [tourPackages]);

  return (
    <div className="container-fluid">
      <h1 className="heading">Our Packages</h1>
      <Swiper
        className="swiper_container"
      >
        {tourPackages.map((tourPackage, index) => (
          <SwiperSlide key={index}>
            <img src={tourPackage.imageUrl} alt={`slide_image_${index}`} />
            <div className="slide-overlay">
              <h1>{tourPackage.name}</h1>
              <p>{tourPackage.description}</p>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
};

export default TourPackages;
