// AdventureDetailPage.tsx

import React, { useState,useEffect } from 'react';
import './AdventureDetailPage.css'; // Import your CSS file
import goa from './assets/adventure.jpg';
import CartButton from './CartButton';
import { Row } from 'react-bootstrap';
import { Link, useParams } from 'react-router-dom'; 

const AdventureDetailPage: React.FC = () => {
  const [content, setContent] = useState<string>('');
  const [selectedView, setSelectedView] = useState<'description' | 'reviews'>('description');
  const { id } = useParams<{ id: string }>(); 
  const handleContentChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setContent(e.target.value);
  };

  const handleAddToCart = (quantity: number) => {
    console.log(`Adding to cart: Quantity - ${quantity}`);
  };

  const handleViewSelection = (view: 'description' | 'reviews') => {
    setSelectedView(view);
  };
  const [tourPackage, setTourPackage] = useState<TourPackage>({
   name:'',
    description: '',


  });
  interface TourPackage {
    name: string;

    description: string;


  }
  useEffect(() => {
    // Fetch data based on the ID when the component mounts
    // Example API call:
    // fetch(`http://localhost:4561/api/adventure-packages/${id}`)
    //   .then(response => response.json())
    //   .then(data => setTourPackage(data))
    //   .catch(error => console.error('Error fetching adventure package:', error));
  }, [id]);
  const handleInputChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>,
  ) => {
    const { name, value } = e.target;
    setTourPackage((prevPackage) => ({
      ...prevPackage,
      [name]: value,
    }));
  };
  return (
    <div className="adventurepage">
      <div className="adventurecontainer">
        <div className="left-sid">
          <div className="text-row">
            <div className="text-row">
              <div className="column">
                <div className="text-element">
                  <img src={goa} className="adventureDetailimage" />
                  <div className="text-row">
                    <div className="text-element">Home / Tour Packages / STANDARD PACKAGE FOR 2-7 PEOPLE</div>
                    <div className="text-element">Tour Packages</div>
                    <div className="text-elementBlue">STANDARD PACKAGE FOR 2-7 PEOPLE</div>
                    <div className="text-elementOrange">G₹6,550.00</div>
                    <div className="text-element">₹ 6,550.00 –2 Days ( 1 Night Stay)</div>
                    <div className="text-element">₹ 8,500.00 –3 Days ( 2 Night Stay)</div>
                    <div className="text-element">₹ 9,999.00 –4 Days ( 3 Night Stay)</div>
                    <div className="text-element">₹ 11,500 –5 Days ( 4 Night Stay)</div>
                    <CartButton onAddToCart={handleAddToCart} />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="hrLine"></div>
        <div className="additionalDivs">
          <div
            className={`additionalDiv ${selectedView === 'description' ? 'selected' : ''}`}
            onClick={() => handleViewSelection('description')}
          >
            Description

          </div>
          <div
            className={`additionalDiv ${selectedView === 'reviews' ? 'selected' : ''}`}
            onClick={() => handleViewSelection('reviews')}
          >
            Reviews

          </div>
        </div>
        {/* Render content based on the selected view */}
        {selectedView === 'description' && (
          <div className="contentContainer">
           <div className="text-elementunderline">Trip Outline</div>
           <div className="text-element">Rates mentioned may vary based on demand. Kindly contact us on +91 739-739-0005 before booking the package.
You can modify the package with self-drive bikes/ cars by contacting our customer support.
Tour itinerary will be shared based on your arrival/ departure timing & mode of transport. Goa is active 24 hours. Unlike other destinations, you can experience a whole new lifestyle at night. Avoid late night parties & casino if you have Dudhsagar/ Scuba Diving/ Hot Air Balloon/ Paragliding/Motoring booking the next day early morning.</div>
<div className="text-elementunderline">Trip Instruction</div>
<div className="text-element">Rates mentioned may vary based on demand. Kindly contact us on +91 739-739-0005 before booking the package
Goa has no restrictions on dress code. You can wear your favourite dress of choice
SCAM ALERT: Stay away from guides roaming on roads & gamblers on beach
Child less than 3 years Free of Cost (Maximum 1 child per couple)
Child 3 years to 9 years need to pay 40% of Adult (Maximum 1 child per couple)
Age 10 years and above considered as Adult</div>
<div className="text-elementunderline">Trip Includes</div>
<div className="text-element">1 Room for up to 4 pax & 2 rooms for 5 – 7 paxc/n
Standard AC Room with Swimming Pool
Stay in Calangute/ near by
Airport/ Railway Station 2 Way Transfers
1 Day North Goa Sightseeing
1 Day South Goa Sightseeing
All Transfers & Sightseeing by AC Cab
Parking, Toll & Driver Bata
24×7 Customer Support
Multilingual Local Support
All Applicable Taxes</div>
          </div>
        )}
        {selectedView === 'reviews' && (
          <div className="contentContainer">
             <div className="reviewcontainer">
             <div className="text-element">Add a review</div>
             <div className="text-element">Your email address will not be published. Required fields are marked *</div>
             <div className="text-element">Your review</div>
             <div style={{ marginBottom: '15px' }}>

          <br />
          <textarea
  id="description"
  name="description"
  value={tourPackage.description}
  onChange={handleInputChange}
  required
  rows={4} // Adjust the number of rows as needed
  cols={195} // Adjust the number of columns as needed
  style={{ resize: 'vertical' }} // Optional: Allow vertical resizing
/>
<div className="horizontalContainer">
      <div className="horizontalFields">
      <div className="formField">
        <Row>
            <div>Name*</div>
            <input type="text" id="name" name="name" placeholder="Enter your name" className="customInput" />
            </Row>
          </div>
        <div className="formField">
            <Row>
            <div>Email*</div>
          <input type="email" id="email" name="email" placeholder="Enter your email" className="customInput" />
          </Row>
        </div>
      </div>
    </div>
    <button type="submit" className="submitButton">
    Submit
  </button>
        </div>

          </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdventureDetailPage;
