// ViewCart.tsx

import React, { useState } from 'react';
import './viewCart.cs';

interface CartItem {
  name: string;
  quantity: number;
  price: number;
}

interface ViewCartProps {
  cartItems: CartItem[];
}

const ViewCart: React.FC<ViewCartProps> = ({ cartItems }) => {
  const [cart, setCart] = useState<CartItem[]>(cartItems);

  const handleIncrementQuantity = (index: number) => {
    const updatedCart = [...cart];
    updatedCart[index].quantity += 1;
    setCart(updatedCart);
  };
const handleDecrementQuantity = (index: number) => {
    const updatedCart = [...cart];
    if (updatedCart[index].quantity > 1) {
      updatedCart[index].quantity -= 1;
      setCart(updatedCart);
    }
  };
  return (
    <div className="view-cart-container">
      <h2>Your Cart</h2>
      <table className="cart-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Quantity</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {cart.map((item, index) => (
            <tr key={index}>
              <td>{item.name}</td>
              <td>
                <div className="quantity-container">
                  <span className="decrement" onClick={() => handleDecrementQuantity(index)}>
                    -
                  </span>
                  <span className="quantity">{item.quantity}</span>
                  <span className="increment" onClick={() => handleIncrementQuantity(index)}>
                    +
                  </span>
                </div>
              </td>
              <td>${item.price * item.quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ViewCart;
