// ReviewBody.tsx
import React from 'react';
import { Card, CardSubtitle, CardText, CardTitle, CardBody, CardImg } from 'reactstrap';

interface ReviewBodyProps {
  firstName: string;
  lastName: string;
  profilePic: string;
  stars: number;
  comment: string;
  timestamp: number;
}

const ReviewBody: React.FC<ReviewBodyProps> = ({
  firstName,
  lastName,
  profilePic,
  stars,
  comment,
  timestamp,
}) => {
  return (
    <Card>
      <CardBody>
        <CardTitle tag="h1">Reviews Page</CardTitle>
        <div className="reviews-top">
          <div className="user-details">
            <CardImg
              className="avatar"
              src={
                profilePic ||
                'https://images.pexels.com/photos/7129713/pexels-photo-7129713.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500'
              }
              alt="user avatar"
            />

            <CardSubtitle className="mb-2 text-muted" tag="h6">
              {firstName} {lastName || 'John Doe'}
            </CardSubtitle>
            {[...Array(stars || 5)].map((star, index) => (
              <CardSubtitle key={index} tag="h5">
                ‚≠ê
              </CardSubtitle>
            ))}
          </div>
          <div className="reviews-body">
            <CardText>
              {comment ||
                'Lorem ipsum dolor sit amet consectetur adipisicing elit. Aut reiciendis delectus dignissimos, nisi pariatur fuga officiis itaque fugiat! Quibusdam accusantium quae beatae vel.Quas possimus reprehenderit sequi quia nesciunt sunt!'}
            </CardText>
          </div>
          <CardText>
            <small className="text-muted text-bold">{timestamp || '3 mins ago'}</small>
          </CardText>
        </div>
      </CardBody>
    </Card>
  );
};

export default ReviewBody;
