import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';

import 'swiper/swiper-bundle.css'; // Add this line
import './tourPackages.css'; // You can keep your custom styles here

import SwiperCore, { Pagination, Navigation } from 'swiper';
import slide_image_1 from '../assets/image1.jpg';
import slide_image_2 from '../assets/image2.jpg';
import slide_image_3 from '../assets/image3.jpg';
import slide_image_4 from '../assets/image5.png';
import slide_image_5 from '../assets/image6.png';
import slide_image_6 from '../assets/image7.jpg';
import slide_image_7 from '../assets/image1.jpg';

const SlideText: React.FC<{ title: string; description: string }> = ({ title, description }) => {
  return (
    <div className="slide-text">
      <h1>{title}</h1>
      <p>{description}</p>
    </div>
  );
};

const TourPackages: React.FC = () => {
  return (
    <div className="container-fluid ">
      <h1 className="heading">Our Packages</h1>
      <Swiper
        effect={'coverflow'}
        grabCursor={true}
        centeredSlides={true}
        loop={true}
        slidesPerView={'auto'}
        coverflowEffect={{
          rotate: 0,
          stretch: 0,
          depth: 100,
          modifier: 2.5,
        }}
        pagination={{ el: '.swiper-pagination', clickable: true }}
        navigation={{
          nextEl: '.swiper-button-next',
          prevEl: '.swiper-button-prev',
        }}
        className="swiper_container"
      >
        <SwiperSlide>
          <img src={slide_image_1} alt="slide_image" />
          <div className="slide-overlay">
            <h1>Slide 1</h1>
            <p>This is the first slide.</p>
          </div>
        </SwiperSlide>
        <SwiperSlide style={{ backgroundImage: 'linear-gradient(to right, rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0))' }}>
          <img src={slide_image_2} alt="slide_image" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={slide_image_3} alt="slide_image" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={slide_image_4} alt="slide_image" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={slide_image_5} alt="slide_image" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={slide_image_6} alt="slide_image" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={slide_image_7} alt="slide_image" />
        </SwiperSlide>

        <div className="slider-controler">
          <div className="swiper-button-prev slider-arrow">{/* <ion-icon name="arrow-back-outline"></ion-icon> */}</div>
          <div className="swiper-button-next slider-arrow">{/* <ion-icon name="arrow-forward-outline"></ion-icon> */}</div>
          <div className="swiper-pagination"></div>
        </div>
      </Swiper>
    </div>
  );
};

export default TourPackages;
