// ContactCardList.tsx

import React, { useEffect, useState } from 'react';
import { Card, CardBody, CardTitle, CardText, Row, Col } from 'react-bootstrap';
import slide_image_2 from '../assets/image2.jpg';
import slide_image_3 from '../assets/image3.jpg';
import slide_image_4 from '../assets/image5.png';
import slide_image_5 from '../assets/image6.png';
import slide_image_6 from '../assets/image7.jpg';
import './places.css'; // Import the CSS file

interface AdventurePackage {
  name: string;
  rate: string;
  description: string;
  imageUrl: string;
  tripOutline: string;
  tripInstruction: string;
  tripIncludes: string;
  id:string;
}


const ContactCard = ({ AdventurePackage }: { AdventurePackage: AdventurePackage }) => {
  const cardStyles = {
    width: '250px', // Set the desired width
    height: '300px', // Set the desired height
  };
  return (
    <Col md={3} className="col-cardStyles" >
      <Card key={AdventurePackage.id} >
        <div className="card-image">
          <img src={AdventurePackage.imageUrl} alt="Contact"  />

          <div className="overlay-text-bottom">{AdventurePackage.description}</div>
        </div>
      </Card>
    </Col>

  );
};

const ContactCardList: React.FC = () => {
  const [data, setData] = useState<AdventurePackage[]>([]);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:4561/api/adventure-packages');
        const result = await response.json();
        setData(result);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="card-list-container" style={{ padding: '2rem 2rem' }}>
      <div className="heading-container">
        <h3>Top Destinations</h3>
      </div>
      <div className="space"></div>
      <Row className='row-gap3'>
        {data.map((contact) => (
          <ContactCard key={contact.id} contact={contact} />
        ))}
      </Row>
    </div>
  );
};

export default ContactCardList;
