// Import React and other necessary modules
import React, { useState } from 'react';
import './viewCart.css';

// Define the CartItem interface
interface CartItem {
  id: number;
  name: string;
  quantity: number;
  price: number;
}

// Define the ViewCartProps interface
interface ViewCartProps {
  cartItems: CartItem[];
  onRemoveItem: (id: number) => void;
}

// Define the ViewCart component
const ViewCart: React.FC<ViewCartProps> = ({ cartItems, onRemoveItem }) => {
  const [cart, setCart] = useState<CartItem[]>(cartItems);

  const handleIncrementQuantity = (id: number) => {
    const updatedCart = cart.map((item) =>
      item.id === id ? { ...item, quantity: item.quantity + 1 } : item
    );
    setCart(updatedCart);
  };

  const handleDecrementQuantity = (id: number) => {
    const updatedCart = cart.map((item) =>
      item.id === id && item.quantity > 1 ? { ...item, quantity: item.quantity - 1 } : item
    );
    setCart(updatedCart);
  };

  return (
    <div className="view-cart-container">
      <h2>Your Cart</h2>
      <table className="cart-table">
        <thead>
          <tr>
            <th>Image</th>
            <th>Name</th>
            <th>Quantity</th>
            <th>Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {cart.map((item) => (
            <tr key={item.id}>
              <td>
                {/* Assuming you have an image URL in your CartItem */}
                <img src={item.imageUrl} alt={item.name} style={{ width: '50px', height: '50px' }} />
              </td>
              <td>{item.name}</td>
              <td>
                <div className="quantity-container">
                  <span className="decrement" onClick={() => handleDecrementQuantity(item.id)}>
                    -
                  </span>
                  <span className="quantity">{item.quantity}</span>
                  <span className="increment" onClick={() => handleIncrementQuantity(item.id)}>
                    +
                  </span>
                </div>
              </td>
              <td>${item.price * item.quantity}</td>
              <td>
                <button onClick={() => onRemoveItem(item.id)}>Remove</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ViewCart;
