          description: '',
          imageUrl: imageUrl,
          tripOutline: '',
          tripInstruction: '',
          tripIncludes: '',
          daysPackages: [],
  // Add any additional properties from your API response
}

const TourPackages: React.FC = () => {
  const [tourPackages, setTourPackages] = useState<TourPackage[]>([]);

  useEffect(() => {
    const fetchTourPackages = async () => {
      try {
        const response = await axios.get<TourPackage[]>('http://localhost:4561/api/tour-packages');
        setTourPackages(response.data);
      } catch (error) {
        console.error('Error fetching tour packages:', error);
      }
    };

    fetchTourPackages();
  }, []);

  return (
    <div className="container-fluid">
      <h1 className="heading">Our Packages</h1>
      <Swiper
        effect={'coverflow'}
        grabCursor={true}
        centeredSlides={true}
        loop={true}
        slidesPerView={'auto'}
        coverflowEffect={{
          rotate: 0,
          stretch: 0,
          depth: 100,
          modifier: 2.5,
        }}
        pagination={{ el: '.swiper-pagination', clickable: true }}
        navigation={{
          nextEl: '.swiper-button-next',
          prevEl: '.swiper-button-prev',
        }}
        className="swiper_container"
      >
        {tourPackages.map((tourPackage) => (
          <SwiperSlide key={tourPackage._id}>
            <img src={tourPackage.imageUrl} alt={`slide_image_${tourPackage._id}`} />
            <div className="slide-overlay">
              <h1>{tourPackage.name}</h1>
              <p>{tourPackage.description}</p>
            </div>
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
};

export default TourPackages;
