const SpecialOffers: React.FC = () => {
  return (
    <div className="container">
      {/* Left side with image view */}
      <div className="left-side">
        <img
          src={process.env.PUBLIC_URL + 'specialOffers.png'}
          alt="Sample"
          className="image-view"
        />
      </div>

      {/* Right side with rows of texts */}
      <div className="right-side">
        {/* First row */}

        <h3>Day Out Holydays</h3>
        <h1>Special Offers</h1>
        <div className="text-row">


        <div className="text-row">

  <div className="column">

    <div className="text-element">
    <img src="path_to_your_image.jpg" className="profile-image" />
    <div className="text-row">
          <div className="text-element">Start From: â‚¹3599</div>
          <div className="text-element">Goa College Package</div>
          <div className="text-element">Our packages are different unique in goa make use of our packages in special offers.</div>
        </div>
  </div>
</div>


        {/* Second row */}
        <div className="text-row">
          <div className="text-element">Text 4</div>
          <div className="text-element">Text 5</div>
          <div className="text-element">Text 6</div>
        </div>

        {/* Third row */}
        <div className="text-row">
          <div className="text-element">Text 7</div>
          <div className="text-element">Text 8</div>
          <div className="text-element">Text 9</div>
        </div>

        {/* ... (additional rows as needed) */}
      </div>
      </div>
      </div>
      </div>
  );
};

export default SpecialOffers;
