// ReviewItem.tsx
import React from 'react';
import { View, Text, Image, StyleSheet, } from 'react-native';

interface ReviewItemProps {
  review: {
    id: string;
    text: string;
    photo: string;
  };
}

const ReviewItem: React.FC<ReviewItemProps> = ({ review }) => {
  return (
    <View style={styles.container}>
      <Image source={{ uri: review.photo }} style={styles.photo} />
      <Text>{review.text}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    margin: 10,
    padding: 10,
    borderRadius: 8,
    borderWidth: 1,
    borderColor: '#ddd',
    width: 200,
    height: 200,
  },
  photo: {
    width: '100%',
    height: '80%',
    borderRadius: 8,
    marginBottom: 10,
  },
});

export default ReviewItem;
