import React, { useState, ChangeEvent, FormEvent } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';

import 'swiper/css';
import 'swiper/css/effect-coverflow';
import 'swiper/css/pagination';
import 'swiper/css/navigation';
import './tourPackages.css';

import axios from 'axios';

import './tourPackages.css';

SwiperCore.use([EffectCoverflow, Pagination, Navigation]);

interface TourPackage {
  name: string;
  description: string;
  imageUrl: string;
}

const TourPackages: React.FC = () => {
  const [tourPackages, setTourPackages] = useState<TourPackage[]>([]);

  useEffect(() => {
    const fetchTourPackages = async () => {
      try {
        const response = await axios.get<TourPackage[]>('http://localhost:4561/api/tour-packages');
        setTourPackages(response.data);
      } catch (error) {
        console.error('Error fetching tour packages:', error);
      }
    };

    fetchTourPackages();
  }, []);

  return (
    <div className="container-fluid">
      <h1 className="heading">Our Packages</h1>
      <Swiper
        effect={'coverflow'}
        grabCursor={true}
        centeredSlides={true}
        loop={true}
        slidesPerView={'auto'}
        coverflowEffect={{
          rotate: 0,
          stretch: 0,
          depth: 100,
          modifier: 2.5,
        }}
        pagination={{ el: '.swiper-pagination', clickable: true }}
        navigation={{
          nextEl: '.swiper-button-next',
          prevEl: '.swiper-button-prev',
        }}
        className="swiper_container"
      >
        {tourPackages.map((tourPackage, index) => (
          <SwiperSlide key={index}>
            <img src={tourPackage.imageUrl} alt={`slide_image_${index}`} />
            <div className="slide-overlay">
              <h1>{tourPackage.name}</h1>
              <p>{tourPackage.description}</p>
            </div>
          </SwiperSlide>
        ))}

        <div className="slider-controler">
          <div className="swiper-button-prev slider-arrow"></div>
          <div className="swiper-button-next slider-arrow"></div>
          <div className="swiper-pagination"></div>
        </div>
      </Swiper>
    </div>
  );
};

export default TourPackages;
