// ContactCardList.tsx

import React, { useEffect, useState } from 'react';
import { Card, Button } from 'react-bootstrap';
import './places.css'; // Import the CSS file

interface AdventurePackage {
  name: string;
  rate: string;
  description: string;
  imageUrl: string;
  tripOutline: string;
  tripInstruction: string;
  tripIncludes: string;
}

const ContactCard: React.FC<AdventurePackage> = ({ name, rate, description, imageUrl, tripOutline, tripInstruction, tripIncludes }) => {
  const cardStyles = {
    width: '250px', // Set the desired width
    height: '300px', // Set the desired height
  };
  return (

    <div className="col-lg-3 col-md-6 col-sm-6 hover_effect">
       <Card className="my-2 shadow-0" style={cardStyles}>
      <Card className="my-2 shadow-0">
        <a href="#" className="">
          <div className="mask" style={{ height: '50px' }}>
            <div className="d-flex justify-content-start align-items-start h-100 m-2">
              <h6><span className="badge bg-danger pt-1">New</span></h6>
            </div>
          </div>
          <Card.Img src={imageUrl} className="rounded-2" style={{ aspectRatio: '1 / 1' }} />
        </a>
        <Card.Body className="p-0 pt-3">
          <a href="#!" className="btn btn-light border px-2 pt-2 float-end icon-hover">
            <i className="fas fa-heart fa-lg px-1 text-secondary"></i>
          </a>
          <Card.Title>{rate}</Card.Title>
          <Card.Text className="mb-0"><b>{name}</b></Card.Text>
          <Card.Text className="text-muted"><b>{description}</b></Card.Text>
          {/* Add more content as needed */}
        </Card.Body>
      </Card>
      </Card>
    </div>
  );
};

const ContactCardList: React.FC = () => {
  const [data, setData] = useState<AdventurePackage[]>([]);
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:4561/api/adventure-packages');
        const result = await response.json();
        setData(result);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <section>
      <div className="container my-5">
        <header className="mb-4">
          <h3>Feature products</h3>
        </header>

        <div className="row">
          {data.map((item, index) => (
            <ContactCard key={index} {...item} />
          ))}
        </div>
      </div>
    </section>
  );
};

export default ContactCardList;
